export function MovieList({ movies, onSetSelectedMovieID }) {
  return (
    <ul className="list list-movies">
      {movies?.map((movie) => (
        <Movie
          movie={movie}
          key={movie.imdbID}
          onSetSelectedMovieID={onSetSelectedMovieID}
        />
      ))}
    </ul>
  );
}
